mapper.select:
SELECT CAR_DTL_ID, CAR_INFO.CAR_ID, CAR_INFO.MANUFACTURER, CAR_INFO.MODEL, CAR_INFO.DETAILED_MODEL, CAR_INFO.MODEL_YEAR, CAR_INFO.DRIVEN_DIST, CAR_INFO.FUEL FROM CAR_DETAILE_INFO JOIN CAR_INFO ON CAR_INFO.CAR_ID = CAR_DETAILE_INFO.CAR_ID WHERE 1 = 1
AND MANUFACTURER = #{manufacturer}
AND MODEL = #{model}
AND CONCAT(MANUFACTURER, MODEL, DETAILED_MODEL) LIKE CONCAT ('%', #{keyword}, '%')
